complexParameter.core:text:
// ID der Brücke bestimmen
def brueckeID = _source.p.id.value()
brueckeID = 'WatercourseLink_' + brueckeID

// Wurde eine passende AX_Gewasserachse gefunden? Falls ja, ID bestimmen:
def gewaesserAchseID = _source.links.AX_Fliessgewaesser.p.id.value()
if (gewaesserAchseID) {
	gewaesserAchseID = 'WatercourseLink_' + gewaesserAchseID
	_target {
		element {
			// Zuerst kommt die Gewässerachse.
			href( "#$gewaesserAchseID" )
		}
		element {
			// Dann kommt die Brücke, da dieser die andere Gewässerachse überquert.
			href( "#$brueckeID" )
		}
	}
}

